    //Összeadja az items listán a GetMaxResource értékét, majd hozzáaadja az alap értéket
    public void AddResource(Resource ammount)
    {
        int maxResource = 20;
        for(InvItem item : items)
        {
            maxResource += item.GetMaxResource();
        }
        
        resource.Add(ammount);
    }
    
