    //Craftol egy vírust
    public void CraftVirus(GeneticCode code)
    {
        for(InvItem item : items)
        {
            if(!item.CanCraft())
            {
                return;
            }
        }
        Resource cost = code.GetCost();
        //has enough resource:
        resource.Remove(cost);
        Agent created = code.CreateVirus();
        AddAgentToStash(created);
    }
    
